    @Test
    public void schweineTest() throws Exception {
        String foo = "asdf-\"Hallo\"-fdsa";
        String bar = foo.replaceAll("\"", "\\\\\"");
        assertNotNull(foo);
        assertTrue("hello:bello".split(":").length == 2);
        assertTrue("bello".split(":").length == 1);
    }

